import React from 'react';
import { StyleSheet, TouchableOpacity, TouchableOpacityProps } from 'react-native';
import { Divider, Text } from '@ui-kitten/components';

interface SectionProps extends TouchableOpacityProps {
  title: string;
  sub_title: string;
  children?: React.ReactNode;
}

export const Notification = (props: SectionProps): React.ReactElement<TouchableOpacityProps> => {

  const { style, title, children, sub_title, ...touchableOpacityProps } = props;

  return (
    <React.Fragment>
      <TouchableOpacity
        activeOpacity={1.0}
        {...touchableOpacityProps}
        style={[styles.container, style]}>
        <Text
        category='s2'>
            {title}
            <Text
            category='label'
            style={{fontWeight:'100'}}>
                {sub_title}
            </Text>
        </Text>
      </TouchableOpacity>
      <Divider/>
    </React.Fragment>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',      
  },
});
