import React from 'react';
import {
    ScrollView,
    StyleSheet,
    Alert,
    ImageBackground, 
    View,
    LogBox
} from 'react-native';
import { Button, Card, Input, List, StyleService, Text, useStyleSheet } from '@ui-kitten/components';
import   backgroundImage  from '../auth/assets/image-background.jpg';
import titleImage  from './assets/title-image.jpg';

export function HomeLayout({ navigation }){
    LogBox.ignoreLogs([
        'VirtualizedLists should never be nested', // TODO: Remove when fixed
      ]);

    const [ value, setValue ] = React.useState('');



    const styles = useStyleSheet(themedStyles);

    const dataFearturedRoom = new Array(8).fill({
        title: 'Item',
      });
    const onItemPress = () => {
        Alert.alert(
            "Alert Title",
            "My Alert Msg",
            [
              {
                text: "Cancel",
                onPress: () => console.log("Cancel Pressed"),
                style: "cancel"
              },
              { text: "OK", onPress: () => console.log("OK Pressed") }
            ],
            { cancelable: false }
          );
    };      



    const renderImageFeaturedRoom = () => (

        <ImageBackground
          style={styles.itemHeader}
          source={titleImage}
          resizeMode='stretch'
        />
      );
    const renderFeaturedRoom = (info) => (
        <View style={styles.boxShadow}>
            <Card
            style={styles.item}
            header={()=>renderImageFeaturedRoom(info)}
            onPress={onItemPress}>
            <Text category='h6' numberOfLines={1} style={{fontWeight:'500'}}>
                Cho thuê chợ Bến Thành số lượng cực lớn
            </Text>
            <Text
                style={styles.itemContent}
                appearance='hint'
                category='s2'
                >
                273 An Dương Vương, Phường 5, Quận 3
            </Text>
            <Text
                style={styles.subItemContent}
                appearance='hint'
                category='c1'
                status='danger'
                >
                10 triệu / phòng
            </Text>           
            </Card>
        </View>
      );

    return (
        <ScrollView style={styles.container}>
            <ImageBackground
                style={styles.image}
                source={backgroundImage}
            />
            <Card
                style={styles.searchCard}
                disabled={true}

            >
                <Input
                    size='medium'
                    placeholder='Tìm kiếm theo quận, tên đường'
                    value={value}

                    onChangeText={setValue}
                />
                <View style={styles.searchView}>
                    <Button 
                    style={styles.searchButton}
                    appearance='filled'>
                        Tìm nâng cao
                    </Button>
                    <Button 
                    style={styles.searchButton}
                    appearance='outline'>
                        Đăng phòng trọ
                    </Button>
                </View>
            </Card>

            <Text
                style={styles.sectionLabel}
                category='s1'>
                Xu hướng tìm kiếm
            </Text>     
            <Text
                style={styles.sectionLabel}
                category='s1'>
                Phòng nổi bật
            </Text>

            <List
                style={styles.list}
                contentContainerStyle={styles.listContent}
                data={dataFearturedRoom}
                renderItem={renderFeaturedRoom}
            />

        </ScrollView>
      );
    };

    const themedStyles = StyleService.create({
      container: {
        backgroundColor: 'background-basic-color-2',
      },
      image: {
        height: 150,
      },
      searchCard: {
        marginTop: -70,
        margin: 16,

      },
      title: {
        width: '65%',
      },
      rentLabel: {
        marginTop: 24,
      },
      priceLabel: {
        marginTop: 8,
      },
      detailsList: {
        flexDirection: 'row',
        marginHorizontal: -4,
        marginVertical: 8,
      },
      detailItem: {
        marginHorizontal: 4,
        borderRadius: 16,
      },
      optionList: {
        flexDirection: 'row',
        marginHorizontal: -4,
        marginVertical: 8,
      },
      sectionLabel: {
        fontWeight:'500',
        marginHorizontal: 26,
        marginVertical: 8,
      },
      optionItem: {
        marginHorizontal: 4,
        paddingHorizontal: 0,
      },
      headerTitle: {
        marginVertical: 8,
      },
      list: {
        flex: 1,
      },
      listContent: {
        paddingHorizontal: 26,
        paddingVertical: 8,
      },
      item: {
        marginVertical: 8,
        borderRadius:15,
      },
      itemHeader: {
        height: 125,
      },
      itemContent: {
        marginVertical: 5,
        fontWeight:'400'
      },
      subItemContent:{
        fontWeight:'400'
      },
      boxShadow:{
          shadowColor : '#000',
          shadowOpacity : 0.10,
          shadowRadius : 3,
          shadowOffset : {width:0,height:2}
      },
      searchView:{
        flex:1,
        flexDirection:'row',

      },
      searchButton:{
          margin: 7,
      },
    });
